mapper.select:
	  SELECT 
	  	i.`ITEM_ID`,
		i.`MALL_CAT_ID`,i.`ITEM_CODE`,i.`MCHT_ID`,i.`MCHT_NAME`
		,i.`ITEM_MCHT_NO`,i.`ITEM_NAME`,i.`ITEM_SHORT_NAME`
		,i.`ITEM_DESC`,i.`ITEM_DESC1`,i.`ITEM_DESC2`,i.`ITEM_DESC3`
		,i.`ITEM_DESC4`,i.`ITEM_COST_PRICE`,i.`ITEM_MARKET_PRICE`
		,i.`ITEM_SALE_PRICE`,i.`ITEM_SALE_NUM`,i.`ITEM_NUM`,i.`ITEM_VALID_START`,
		`ITEM_VALID_END`,i.`ITEM_SALE_ATTR`,i.`ITEM_APPR_STATE`,i.`ITEM_UD_STATE`
		,i.`ITEM_STATE_TIME`,i.`ITEM_SCHDLON_STATE`,i.`ITEM_SCHDLON_TIME`
		,i.`ITEM_SCHDLDOWN_TIME`,i.`ITEM_FORCEOFF_STATE`,i.`ITEM_FORCEOFF_TIME`
		,i.`ITEM_FORCEOFF_REASON`,i.`ITEM_SEO_KEYWORD`,i.`ITEM_SEO_DESC`
		,i.`ITEM_MAX_BUY_NUMS`,i.`ITEM_SALE_FLAG`,i.`OPTL_PAY_TYPE_STR`,i.`DATA_VERSION`
		,i.`CREATOR`,i.`CREATE_TIME`,i.`MODIFYER`,
		i.`MODIFY_TIME`,
		m.`MALL_CAT_NAME`,
		p.`ITEM_PIC_URL`,
		iit.`ITEM_TYPE_NAME` 
		FROM
		`ic_item` i 
		LEFT JOIN `ic_mall_cat` m 
		ON i.`MALL_CAT_ID` = m.`MALL_CAT_ID` 
		LEFT JOIN `ic_item_picture` p 
		ON i.item_id = p.`ITEM_ID` 
		LEFT JOIN `ic_item_type` iit 
		ON i.`ITEM_TYPE` = iit.`ITEM_TYPE` 
		WHERE i.data_state = 1 
	   
	   and i.ITEM_ID = #{itemId,jdbcType=DECIMAL}
	   
	   
        and i.MALL_CAT_ID = #{mallCatId,jdbcType=DECIMAL}
      
      
       and i. ITEM_CODE = #{itemCode,jdbcType=DECIMAL}
      
      
        and i.ITEM_TYPE = #{itemType,jdbcType=DECIMAL}
      
      
        and i.ITEM_NAME = #{itemName,jdbcType=VARCHAR}
      
      
        and i.ITEM_MARKET_PRICE = #{itemMarketPrice,jdbcType=DECIMAL}
      
      
        and i.ITEM_SALE_PRICE = #{itemSalePrice,jdbcType=DECIMAL}
      
      
        and i.ITEM_SALE_NUM = #{itemSaleNum,jdbcType=DECIMAL}
      
      
        and i.ITEM_NUM = #{itemNum,jdbcType=DECIMAL}
      
      
        and i.ITEM_VALID_START = #{itemValidStart,jdbcType=TIMESTAMP}
      
      
        and i.ITEM_VALID_END = #{itemValidEnd,jdbcType=TIMESTAMP}
      
      
        and i.ITEM_SALE_ATTR = #{itemSaleAttr,jdbcType=VARCHAR}
      
      
        and i.ITEM_APPR_STATE = #{itemApprState,jdbcType=DECIMAL}
      
      
        and i.ITEM_UD_STATE = #{itemUdState,jdbcType=DECIMAL}
      
      
        and i.ITEM_STATE_TIME = #{itemStateTime,jdbcType=TIMESTAMP}
      
      
        and i.ITEM_SCHDLON_STATE = #{itemSchdlonState,jdbcType=DECIMAL}
