mapper.select:
	select  from sc_shop 
	where data_state = 1 
	
      and MCHT_ID = #{mchtId,jdbcType=DECIMAL} 
    
    
      and SHOP_SHORT_NAME = #{shopShortName,jdbcType=VARCHAR} 
    
    
      and SHOP_NAME = #{shopName,jdbcType=VARCHAR} 
    
    
      and SHOP_DESC = #{shopDesc,jdbcType=VARCHAR} 
    
    
      and SHOP_LONGITUDE = #{shopLongitude,jdbcType=DECIMAL} 
    
    
      and SHOP_LATITUDE = #{shopLatitude,jdbcType=DECIMAL} 
    
    
      and SHOP_NEAR_INF = #{shopNearInf,jdbcType=VARCHAR} 
    
    
      and SHOP_BUS_INFO = #{shopBusInfo,jdbcType=VARCHAR} 
    
    
      and SHOP_STATE = #{shopState,jdbcType=DECIMAL} 
    
    
      and PROV_ID = #{provId,jdbcType=DECIMAL} 
    
    
      and CITY_ID = #{cityId,jdbcType=DECIMAL} 
    
    
      and REGION_ID = #{regionId,jdbcType=DECIMAL} 
    
    
      and SHOP_ADDRESS = #{shopAddress,jdbcType=VARCHAR} 
    
    
      and SHOP_PIC_URL = #{shopPicUrl,jdbcType=VARCHAR} 
    
    
      and CUSTSRV_PHONE = #{custsrvPhone,jdbcType=VARCHAR} 
    
    
      and CONTACT_NAME = #{contactName,jdbcType=VARCHAR} 
    
    
      and CONTACT_PHONE = #{contactPhone,jdbcType=VARCHAR} 
    
    
      and CONTACT_PHONE2 = #{contactPhone2,jdbcType=VARCHAR} 
    
    
      and CONTACT_EMAIL = #{contactEmail,jdbcType=VARCHAR} 
    
    
      and CREATOR = #{creator,jdbcType=DECIMAL} 
    
    
      and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP} 
    
    
      and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP} 
    
    
      and MODIFYER = #{modifyer,jdbcType=DECIMAL}
