mapper.select:
SELECT tg.GOODS_ID, tg.ORDER_ID, tg.ITEM_CHK_CODE, tg.MCHT_ID, tg.MCHT_NAME, tg.ITEM_ID, tg.ITEM_TYPE, tg.ITEM_CODE, tg.ITEM_NAME, tg.ITEM_ACT_PRICE, tg.GOODS_DEAL_FEE, tg.RTNISTD_STATE, tg.RTNISTD_RSN_TYPE, tg.RTNISTD_REASON, tg.GOODS_RFD_FEE, tg.USER_ID, tg.USER_NAME, tg.USER_NICKNAME, tg.LOGIN_NAME, tg.USER_PHONENO, tg.ITEM_VALID_START, tg.ITEM_VALID_END, tg.USE_TIME, tg.USE_RESULT, tg.SHOP_ID, tg.SHOP_SHORT_NAME, tg.SHOP_NAME, tg.EXP_STATE, tg.EXP_TIME, tg.CHECK_STATE, tg.SETTLEMENT_DATE, tg.DATA_STATE, tg.CREATOR, tg.CREATE_TIME, tg.MODIFYER, tg.MODIFY_TIME, tco.`ORDER_TIME` FROM tc_goods tg LEFT JOIN tc_order tco ON tg.`ORDER_ID`= tco.`ORDER_ID` WHERE tg.`DATA_STATE`=1 AND tco.`DATA_STATE`=1
and tg.SHOP_ID=#{shopId}
and tg.ITEM_CODE=#{itemCode}
and tg.ITEM_NAME=#{itemName}
and tg.`EXP_TIME` BETWEEN #{expStartTime} AND #{expEndTime}
and tg.`USE_TIME` BETWEEN #{useStartTime} AND #{useEndTime}
